import React from 'react';
import { useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';

const ProductDescription = () => {
  const { id } = useParams();
  const products = useSelector((state) => state.products.items);
  const product = products.find((p) => p.id === parseInt(id));

  if (!product) {
    return <p>Product not found</p>;
  }

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow-lg">
      <div className="flex flex-col md:flex-row">
        <img 
          src={product.imageUrl} 
          alt={product.name} 
          className="w-full md:w-1/2 h-auto object-cover rounded-lg"
        />
        <div className="md:ml-6 mt-6 md:mt-0">
          <h1 className="text-2xl font-bold mb-4">{product.name}</h1>
          <p className="text-xl text-gray-700 mb-4">{product.price.current.text}</p>
          <p className="text-lg text-gray-600 mb-4">Brand: {product.brandName}</p>
          <p className="text-md text-gray-500 mb-4">Color: {product.colour}</p>
          <p className="text-sm text-gray-400 mb-4">Product Code: {product.productCode}</p>
          <a 
            href={product.url} 
            target="_blank" 
            rel="noopener noreferrer"
            className="inline-block mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            View on Site
          </a>
        </div>
      </div>
      <div className="mt-6">
        <h2 className="text-xl font-semibold mb-4">Additional Images</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {product.additionalImageUrls.map((url, index) => (
            <img 
              key={index} 
              src={url} 
              alt={`${product.name} - additional ${index + 1}`} 
              className="w-full h-auto object-cover rounded-lg"
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductDescription;
