import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import MenuRoundedIcon from '@mui/icons-material/MenuRounded';
import SearchIcon from '@mui/icons-material/Search';
import Avatar from '@mui/material/Avatar';
import ShoppingCartOutlinedIcon from '@mui/icons-material/ShoppingCartOutlined';
import { useDispatch, useSelector } from 'react-redux';
import { selectUserData, logout } from '../redux/userSlice';
import '../styles/Navbar.css';

function Navbar({ onSubmit, onCategoryChange = () => {} }) {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [input, setInput] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const userData = useSelector(selectUserData);
  const isAuthenticated = !!userData.email;

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth >= 768) {
        setIsMenuOpen(false);
      }
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const handleLogout = async () => {
    try {
      const response = await axios.post('http://localhost:5000/logout', {}, { withCredentials: true });
      if (response.status === 200) {
        dispatch(logout());
        navigate('/');
      }
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  const handleCategoryClick = (categoryId) => {
    navigate(`/productsList/${categoryId}`);
  };

  const onSearchSubmit = (e) => {
    e.preventDefault();
    onSubmit(input);
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <>
      <div className="wrapper">
        <div className="logoWrapper">
          <a href="/" className="logoText">
            <h1>YumYam</h1>
          </a>
        </div>
        <div className="menuIcon" onClick={toggleMenu}>
          <MenuRoundedIcon />
        </div>
        <div className="desktopMenu">
          <div className="searchWrapper">
            <div className="searchBarWrapper">
              <form onSubmit={onSearchSubmit}>
                <input
                  className="searchInput"
                  type="text"
                  placeholder="Search 2M+ recipes"
                  onChange={(e) => setInput(e.target.value)}
                  value={input}
                />
                <button className="iconButton" type="submit">
                  <SearchIcon />
                </button>
              </form>
            </div>
          </div>
          <div className="profileWrapper">
            {isAuthenticated ? (
              <div className="profileIcon">
                <Avatar alt="Profile" src="/static/images/avatar/1.jpg" sx={{ width: 32, height: 32 }} />
                <Link to="/profile">{`${userData.firstName} ${userData.lastName}`}</Link>
                <button onClick={handleLogout}>Logout</button>
              </div>
            ) : (
              <div className="loginWrapper">
                <Link to="/login">
                  <button>Login</button>
                </Link>
              </div>
            )}
          </div>
          <div className="menuWrapper">
            <div className="menuOption">
              <Link to="/orders">Orders</Link>
            </div>
            <div className="menuOption">
              <Link to="/cart"><ShoppingCartOutlinedIcon/></Link>
            </div>
          </div>
        </div>
        <div className={`dropdownMenu ${isMenuOpen ? 'open' : ''}`}>
          <div className="menuIcon" onClick={toggleMenu}>
            <MenuRoundedIcon />
          </div>
          <div className="searchWrapper">
            <div className="searchBarWrapper">
              <form onSubmit={onSearchSubmit}>
                <input
                  className="searchInput"
                  type="text"
                  placeholder="Search 2M+ recipes"
                  onChange={(e) => setInput(e.target.value)}
                  value={input}
                />
                <button className="iconButton" type="submit">
                  <SearchIcon />
                </button>
              </form>
            </div>
          </div>
          <div className="profileWrapper">
            {isAuthenticated ? (
              <div className="profileIcon">
                <Avatar alt="Profile" src="/static/images/avatar/1.jpg" sx={{ width: 32, height: 32 }} />
                <Link to="/profile">{`${userData.firstName} ${userData.lastName}`}</Link>
                <button onClick={handleLogout}>Logout</button>
              </div>
            ) : (
              <div className="loginWrapper">
                <Link to="/login">
                  <button>Login</button>
                </Link>
              </div>
            )}
          </div>
          <div className="menuWrapper">
            <div className="menuOption">
              <Link to="/orders">Orders</Link>
            </div>
            <div className="menuOption">
              <Link to="/cart"><ShoppingCartOutlinedIcon/></Link>
            </div>
          </div>
        </div>
      </div>
      <div className="categoryBar">
        {/* Categories code */}
        <div className="categoryOption" onClick={() => handleCategoryClick(4209)}>Sneakers</div>
        <div className="categoryOption" onClick={() => handleCategoryClick(6456)}>Women Sneakers</div>
        <div className="categoryOption" onClick={() => handleCategoryClick(4210)}>Accessories</div>
        {/* Add more categories as needed */}
      </div>
    </>
  );
}

export default Navbar;
