import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { auth } from "../Authentication/firebase";
import { createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";

export const signup = createAsyncThunk(
  "user/signup",
  async ({ firstName, email, password }) => {
    const userCredential = await createUserWithEmailAndPassword(auth, email, password);
    return { firstName: firstName, user: userCredential.user };
  }
);

export const login = createAsyncThunk("user/login", async ({ email, password }) => {
  const userCredential = await signInWithEmailAndPassword(auth, email, password);
  return userCredential.user;
});

const initialState = {
  userData: {
    name: "",
    email: "",
  },
  loading: false,
};

const userSlice = createSlice({
  name: "user",
  initialState,
  reducers: {
    getCoordinates(state, action) {
      state.longitude = action.payload.longitude;
      state.latitude = action.payload.latitude;
    },
    logout(state) {
      state.userData = {
        name: "",
        email: "",
      };
    },
    setProfileData(state, action) {
      state.userData.email = action.payload.email;
      state.userData.name = action.payload.name;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(signup.pending, (state) => {
        state.loading = true;
      })
      .addCase(signup.fulfilled, (state, action) => {
        state.loading = false;
        state.userData.name = action.payload.firstName;
      })
      .addCase(signup.rejected, (state) => {
        state.loading = false;
      });

    builder
      .addCase(login.pending, (state) => {
        state.loading = true;
      })
      .addCase(login.fulfilled, (state, action) => {
        state.loading = false;
        state.userData.name = action.payload.displayName || '';
        state.userData.email = action.payload.email || '';
      })
      .addCase(login.rejected, (state) => {
        state.loading = false;
      });
  },
});

export default userSlice.reducer;

export const { getCoordinates, logout, setProfileData } = userSlice.actions;

export const selectUserData = (state) => state.user.userData;
