import React from 'react';

const ProductCard = ({ product }) => {
  const { name, price, colour, brandName, imageUrl } = product;

  return (
    <div className="border rounded-lg overflow-hidden shadow-lg">
      <img src={imageUrl} alt={name} className="w-full h-48 object-cover" />
      <div className="p-4">
        <h2 className="text-lg font-bold mb-2">{name}</h2>
        <p className="text-sm text-gray-700">Brand: {brandName}</p>
        <p className="text-sm text-gray-700">Colour: {colour}</p>
        <p className="text-lg font-semibold mt-2">{price.current.text}</p>
      </div>
    </div>
  );
};

export default ProductCard;
